def loads_alert():
    """
        Metadata to be added in the python script
    """
    msgfrom = """@@@@@msgfrom@@@@@"""
    binding = """@@@@@binding@@@@@"""

    serialisationpickleevent = u"""@@@@@event@@@@@"""

    eventstruct = json.loads(serialisationpickleevent)
    if 'general_status' in eventstruct['mon_devices_doc']:
        eventstruct['general_status'] = eventstruct['mon_devices_doc']['general_status']

    # ---------------------------------------------
    # eventstruct est la structure des informations de l'alerte.
    # ----------------------------------------------------
    return eventstruct, msgfrom.strip(), binding.strip()


class message_email_smtp_ssl:
    def __init__(
            self,
            serverhost,
            porthost,
            compte_email,
            email_password):
        self.serverhost = serverhost
        self.porthost = porthost
        self.compte_email = compte_email
        self.email_password = email_password
        self.fromaddr = 'Alert <%s>' % compte_email
        self.suject_email = "Rapport event"
        self.namefile = None

    def destinaire(self, list_to_addrs_array):
        if isinstance(list_to_addrs_array, basestring):
            self.to_addrs_array = list_to_addrs_array.split(",")
        else:
            self.to_addrs_array = list_to_addrs_array

    def suject(self, suject_email="Rapport event"):
        self.suject_email = suject_email

    def document_attache(self, namefile=None):
        self.namefile = namefile

    def message_text(self, minetype_plain_text=None):
        self.msg_plain_text = minetype_plain_text

    def message_html(self, minetype_html_text=None):
        self.msg_html_text = minetype_html_text

    def connectsend(self):
        if self.msg_plain_text or self.msg_plain_text:
            try:
                self.server = smtplib.SMTP_SSL()  # We use the SMTP_SSL() function instead of SMTP()
                print(self.serverhost)
                print(self.porthost)
                connect_to_server = self.server.connect(
                    self.serverhost, self.porthost) 
                print(connect_to_server)

                hello_from_server = self.server.ehlo()
                logger.debug("hello_from_server %s" % hello_from_server)
                print (hello_from_server)
                self.server.login(self.compte_email, self.email_password)
                return True
            except BaseException:
                print("\n%s" % (traceback.format_exc()))
                return False
        else:
            print("Message undefined")
        return False

    def send_mail(self):
        if self.connectsend():
            msg = MIMEMultipart('alternative')
            msg['From'] = self.fromaddr
            msg['To'] = ','.join(self.to_addrs_array)
            msg['Subject'] = self.suject_email
            msg["Date"] = formatdate(localtime=True)
            # Record the MIME types of both parts - text/plain and text/html.
            if self.msg_plain_text:
                part1 = MIMEText(self.msg_plain_text, 'plain')
                msg.attach(part1)
            if self.msg_html_text:
                part2 = MIMEText(self.msg_html_text, 'html')
                msg.attach(part2)
            if self.namefile:
                if os.path.isfile(self.namefile):
                    suffixe = self.namefile.split('.')
                    if len(suffixe) > 1:
                        suffixe = suffixe[-1]
                    else:
                        suffixe = "dat"
                    fp = open(self.namefile, 'rb')
                    att = email.mime.application.MIMEApplication(
                        fp.read(), _subtype=suffixe)
                    fp.close()
                    att.add_header(
                        'Content-Disposition',
                        'attachment',
                        filename=os.path.basename(
                            self.namefile))
                    msg.attach(att)
            try:
                self.server.sendmail(
                    self.fromaddr,
                    self.to_addrs_array,
                    msg.as_string())
            except smtplib.SMTPException as e:
                print(e)
            self.server.quit()


class message_email_smtp_ssl_tls:
    def __init__(
            self,
            serverhost,
            porthost,
            compte_email,
            email_password):
        self.serverhost = serverhost
        self.porthost = porthost
        self.compte_email = compte_email
        self.email_password = email_password
        self.fromaddr = 'Alert <%s>' % compte_email
        self.suject_email = "Rapport event"
        self.namefile = None

    def destinaire(self, list_to_addrs_array):
        self.to_addrs_array = list_to_addrs_array

    def suject(self, suject_email="Rapport event"):
        self.suject_email = suject_email

    def document_attache(self, namefile=None):
        self.namefile = namefile

    def message_text(self, minetype_plain_text=None):
        self.msg_plain_text = minetype_plain_text

    def message_html(self, minetype_html_text=None):
        self.msg_html_text = minetype_html_text

    def connectsend(self):
        if self.msg_plain_text or self.msg_plain_text:
            try:
                self.server = smtplib.SMTP()  # With TLS, we use SMTP()
                print(self.serverhost)
                print(self.porthost)
                connect_to_server = self.server.connect(
                    self.serverhost, self.porthost)
                print(connect_to_server)
                logger.debug("connect_to_server %s" % connect_to_server)

                hello_from_server = self.server.ehlo()
                print (hello_from_server)
                logger.debug("hello_from_server %s" % hello_from_server)

                self.server.starttls()
                self.server.login(self.compte_email, self.email_password)
                return True
            except BaseException:
                print("\n%s" % (traceback.format_exc()))
                return False
        else:
            print("Message undefined")
        return False

    def send_mail(self):
        if self.connectsend():
            msg = MIMEMultipart('alternative')
            msg['From'] = self.fromaddr
            msg['To'] = ','.join(self.to_addrs_array)
            msg['Subject'] = self.suject_email
            msg["Date"] = formatdate(localtime=True)
            # Record the MIME types of both parts - text/plain and text/html.
            if self.msg_plain_text:
                part1 = MIMEText(self.msg_plain_text, 'plain')
                msg.attach(part1)
            if self.msg_html_text:
                part2 = MIMEText(self.msg_html_text, 'html')
                msg.attach(part2)
            if self.namefile:
                if os.path.isfile(self.namefile):
                    suffixe = self.namefile.split('.')
                    if len(suffixe) > 1:
                        suffixe = suffixe[-1]
                    else:
                        suffixe = "dat"
                    fp = open(self.namefile, 'rb')
                    att = email.mime.application.MIMEApplication(
                        fp.read(), _subtype=suffixe)
                    fp.close()
                    att.add_header(
                        'Content-Disposition',
                        'attachment',
                        filename=os.path.basename(
                            self.namefile))
                    msg.attach(att)
            try:
                self.server.sendmail(
                    self.fromaddr,
                    self.to_addrs_array,
                    msg.as_string())
            except smtplib.SMTPException as e:
                print(e)
            self.server.quit()


def main():
    doc = _template_html_event(eventstruct)
    if eventstruct['mon_rules_comment']['typeserver']:
        emailobj = message_email_smtp_ssl(
            eventstruct['mon_rules_comment']['accountserver'],
            eventstruct['mon_rules_comment']['portserver'],
            eventstruct['mon_rules_comment']['accountemail'],
            eventstruct['mon_rules_comment']['email_password'])
        emailobj.suject(
            suject_email=eventstruct['mon_rules_comment']['suject'])
        emailobj.destinaire([eventstruct['mon_rules_user']])
        emailobj.message_text(minetype_plain_text="ne pas repondre")
        emailobj.message_html(minetype_html_text=doc)
        emailobj.send_mail()


